    public boolean camposVazios(){
        boolean vazios = false; //Os campos est√£o preenchidos
        
        if(Txt_cadastroNome.getText().isEmpty() ||
           Txt_cadastroValor.getText().isEmpty()){
            vazios = true;
            }
    
        return vazios;
    }
